mapper.select:
select
  id, user_id, order_no, product_id, product_name, price, create_time, update_time
from t_order


        and id = #{id}


        and user_id = #{userId}


        and order_no = #{orderNo}


        and product_id = #{productId}


        and product_name = #{productName}


        and price = #{price}


        and create_time = #{createTime}


        and update_time = #{updateTime}
